import React from 'react';
import './App.css';
import Dog from './Dog'

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      allPups: [],
      featuredPupper: null
    }
  }

  componentDidMount() {
    fetch("http://localhost:3000/pups").then(r => r.json()).then(pups => this.setState({allPups: pups}))
  } 

  goDoggo = () => {
    return this.state.allPups.map(pupper => <Dog key={pupper.id} dogInfo={pupper} handleClick={this.showInfo}/> );
  }

  showInfo = (chosenBoy) => {
    console.log(chosenBoy)
    this.setState({featuredPupper: chosenBoy})
  }

render() { 
  return (
    <div className="App">
      <div id="filter-div">
        <button id="good-dog-filter">Filter good dogs: OFF</button>
      </div>
      <div id="dog-bar">
        {this.goDoggo()}

      </div>
      <div id="dog-summary-container">
        <h1>DOGGO:</h1>
        <div id="dog-info">

        </div>
      </div>
    </div>
  )
}}

export default App;
