import React, { Component } from 'react';

class Deets extends Component {

    renderPup = () => {
        if (this.props.bestBoy !== null) {
            return <img src={this.props.bestBoy.image} alt="pupper pic"/>
        };
    }     

    renderName = () => {
        if(this.props.bestBoy !== null) {
            return <h2> {this.props.bestBoy.name} </h2>
        };
    }

    renderGoodBoy = () => {
        if (this.props.bestBoy !== null) {
            return <h2> {this.props.bestBoy.isGoodBoy} </h2>
        };
    }


    render() {
        return (
            <>
            {this.renderPup()}
            {this.renderName()}
            <button handleClick={this.props.handleClick} >button</button>


            </>
        );
    }
}

export default Deets;
