import React, { Component } from 'react';

class Deets extends Component {
    constructor (props){
        super(props)
        this.state = {
            name: null,
            image: null,
            status: null
        }
    }

    renderPup = () => {
        if (this.props.bestBoy !== null) {
            return <img src={this.props.bestBoy.image} alt="pupper pic"/>
        };
    }     

    renderName = () => {
        if(this.props.bestBoy !== null) {
            return <h2> {this.props.bestBoy.name} </h2>
    };
}


    render() {
        return (
            <>
            {this.renderPup()}
            {this.renderName()}
            {this.renderStatus()}

            </>
        );
    }
}

export default Deets;
