import React from 'react';
import './App.css';
import DogBar from './DogBar'

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      allPups: []
    }
  }

  componentDidMount() {
    fetch("http://localhost:3000/pups").then(r => r.json()).then(pups => this.setState({allPups: pups}))
  } 

  goDoggo = () => {
    this.state.allPups.map(dog => <DogBar dogInfo={dog} />);
  }

render() { 
  return (
    <div className="App">
      <div id="filter-div">
        <button id="good-dog-filter">Filter good dogs: OFF</button>
      </div>
      <div id="dog-bar">
        {this.goDoggo()}

      </div>
      <div id="dog-summary-container">
        <h1>DOGGO:</h1>
        <div id="dog-info">

        </div>
      </div>
    </div>
  )
}}

export default App;
